import inquirer from "inquirer";
async function startFunc() {
    const randomNumber = Math.floor(Math.random() * 100 + 1);
    const userNum = await inquirer.prompt([
        {
            type: "input",
            name: "userGuess",
            message: "Enter the guess number between 1 to 100",
        }
    ]);
    const { userGuess } = userNum;
    console.log(`Your Guess: ${userGuess},  Random Guess: ${randomNumber}`);
    if (userGuess === randomNumber) {
        console.log("The Guess Number is Correct");
    }
    else {
        console.log("The Guess Number is Not Correct");
    }
}
async function startAgain() {
    do {
        await startFunc();
        var again = await inquirer.prompt({
            type: "input",
            name: "restart",
            message: "The Game will be continue Press: Y or N",
        });
    } while (again.restart == "Y" || again.restart == "Y" || again.restart == "Y" || again.restart == "Y");
}
startAgain();
